SELECT * FROM (SELECT inner_table.*, ROWNUM AS row_seq FROM (SELECT u.user_id, u.user_name, u.cell_phone, p.prod_no, p.prod_name, p.price, p.image_file, p.prod_detail, p.manufacture_day, t.tran_no, t.order_date, t.tran_code, t.receiver_addr, t.receiver_name FROM users u, product p, transaction t
u.user_id = t.buyer_id AND p.prod_no = t.prod_no AND u.user_id = #{userId}
AND p.prod_no = #{searchKeyword}
AND p.prod_name LIKE '%${searchKeyword}%'
AND p.price BETWEEN #{searchLowPrice} AND #{searchHighPrice}
AND p.price >= #{searchLowPrice}
ORDER BY
p.prod_name
p.prod_name DESC
t.order_date DESC
t.order_date
t.tran_no ) inner_table WHERE ROWNUM <= ${search.currentPage}*${search.pageSize}) WHERE row_seq BETWEEN (${search.currentPage}-1)*${search.pageSize}+1 AND ${search.currentPage}*${search.pageSize}

