u.user_id = t.buyer_id AND p.prod_no = t.prod_no
AND p.prod_no = #{searchKeyword}
AND p.prod_name LIKE '%${searchKeyword}%'
AND p.price BETWEEN #{searchLowPrice} AND #{searchHighPrice}
AND p.price >= #{searchLowPrice}
ORDER BY
p.prod_name
p.prod_name DESC
t.order_date DESC
t.order_date
t.tran_no ) inner_table WHERE ROWNUM <= ${currentPage}*${pageSize}) WHERE row_seq BETWEEN (${currentPage}-1)*${pageSize}+1 AND ${currentPage}*${pageSize}

SELECT COUNT(*) FROM (SELECT u.user_id, u.user_name, u.cell_phone, p.prod_no, p.prod_name, p.price, t.tran_no, t.order_date, t.tran_code FROM users u, product p, transaction t
u.user_id = t.buyer_id AND p.prod_no = t.prod_no
AND u.user_id = #{userId}
AND p.prod_no = #{search.searchKeyword}
AND p.prod_name LIKE '%${search.searchKeyword}%'
AND p.price BETWEEN #{search.searchLowPrice} AND #{search.searchHighPrice}
AND p.price >= #{search.searchLowPrice}
ORDER BY
p.prod_name
p.prod_name DESC
t.order_date DESC
t.order_date
t.tran_no ) countTable
